const resolvers = {
  Query: {
    getAllBooks() {
      console.log(books);
      return books;
    },
    getBookById(parent,args){
        return books.find((b)=>b.id===args.id);
    },
    getBooksByCategory(parent,args){
        return books.filter((b)=>b.genre===args.genre);
    }
  },
  Mutation: {
    addBook(parent, args) {
      const newBook = args;
      books.push(newBook);
      return newBook;
    },
    updateBook(parent, args) {
      const index = books.findIndex((b) => b.id === args.id);
      console.log(index);
      if (index > -1) {
        if (args.title) books[index].title = args.title;
        if (args.genre) books[index].genre = args.genre;
        if (args.author) books[index].author = args.author;
        if (args.img) books[index].img = args.img;
        return books[index];
      }
    },
    deleteBook(parent, args) {
        const index=books.findIndex((b)=>b.id===args.id);
        if(index>-1){
            const el=books.splice(index,1)[0];
            return el;
        }
    },
  },
};
module.exports = { resolvers };
